    function validateUserName(userName){
        if ( typeof(userName) === "string" ){
            if (userName == /^[a-z A-Z 0-9 \-]+$/){
                if ( userName.length >= 3 || userName.length <= 60 ){
                    return "true";
                } else {
                    return "false";
                }
            } else {
                return "false";
            }
        } else {
            return "false";
        }
    }

    function validatePassword(password) {
        if ( password.length >= 3 || password.length <= 60 ){
            if ( password == /^[a-z A-Z 0-9]+$/ ){
                return "true";
            } else {
                return "false";
            }
        } else {
            return "false";
        }
    }

    function validateConfirmPassword ( password, confirmPassword ) {
        if ( password === confirmPassword ){
            return "true";
        } else {
            return "false";
        }
    }

    function validateRegister(userName, password, confirmPassword){
        var condition1 = validateUserName(userName);
        var condition2 = validatePassword(password);
        var condition3 = validateConfirmPassword(password, confirmPassword);

        if (condition1 == "true" && condition2 == "true" && condition3 == "true"){
            return "true";
        } else {
            return "false";
        }
    }

module.exports.validateRegister = validateRegister;